CREATE TABLE topicos ( id BIGSERIAL PRIMARY KEY, estado VARCHAR(50) NOT NULL CHECK (estado IN ('OPEN', 'CLOSED', 'DELETED')), fecha_creacion TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP, mensaje VARCHAR(255) NOT NULL, titulo VARCHAR(255) NOT NULL, ultima_actualizacion TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP, curso_id BIGINT NOT NULL, usuario_id BIGINT NOT NULL, FOREIGN KEY (curso_id) REFERENCES cursos(id), FOREIGN KEY (usuario_id) REFERENCES usuarios(id) );