 CREATE TABLE respuestas ( id BIGSERIAL PRIMARY KEY, borrado BOOLEAN NOT NULL, fecha_creacion TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP, mensaje VARCHAR(255) NOT NULL, solucion BOOLEAN NOT NULL, ultima_actualizacion TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP, topico_id BIGINT NOT NULL, usuario_id BIGINT NOT NULL, FOREIGN KEY (topico_id) REFERENCES topicos(id), FOREIGN KEY (usuario_id) REFERENCES usuarios(id) );
